(1, 'applicant_submit_sadr_email', 'New SADR report :reference_number', '<p>&nbsp;</p>\r\n\r\n<p><img src="data:image/png;base64," /></p>\r\n\r\n<p style="text-align:right"><span style="font-size:12pt"><span style="font-family:&quot;Garamond&quot;,serif"><span style="font-family:Times New Roman,serif">REF: :reference_number</span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:12pt"><span style="font-family:&quot;Garamond&quot;,serif"><span style="font-family:Times New Roman,serif">Dear :reporter_email,</span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:12pt"><span style="font-family:&quot;Garamond&quot;,serif"><span style="font-family:Times New Roman,serif"><u><strong>RE: ACKNOWLEDGEMENT OF RECEIPT OF A SUSPECTED ADVERSE DRUG REACTION (ADR) REPORT :reference_number</strong></u></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:12pt"><span style="font-family:&quot;Garamond&quot;,serif"><span style="font-family:Times New Roman,serif">We acknowledge with thanks receipt of the suspected adverse drug reaction (ADR) report <strong>:reference_number</strong>. The report will be processed and tabled for consideration by the Pharmacovigilance and Clinical Trials Committee. We will notify you of the Committee&rsquo;s decision in due course.</span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:12pt"><span style="font-family:&quot;Garamond&quot;,serif"><span style="font-family:Times New Roman,serif">We appreciate your submission of the report and your support of the national pharmacovigilance programme in promoting patient safety. </span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:12pt"><span style="font-family:&quot;Garamond&quot;,serif"><span style="font-family:Times New Roman,serif">Yours faithfully</span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:12pt"><span style="font-family:&quot;Garamond&quot;,serif"><span style="font-family:Times New Roman,serif"><strong>MEDICINES CONTROL AUTHORITY OF ZIMBABWE</strong></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:12pt"><span style="font-family:&quot;Garamond&quot;,serif"><span style="font-family:Times New Roman,serif"><em>to insert signature</em></span></span></span></p>\r\n\r\n<p style="text-align:left"><span style="font-size:12pt"><span style="font-family:&quot;Garamond&quot;,serif"><span style="font-family:Times New Roman,serif"><strong>DIRECTOR - GENERAL</strong></span></span></span></p>\r\n', 'email', 'Email sent to reporter after submitting SADR report', '2017-12-05 21:58:48', '2017-12-16 19:31:44', NULL),
(2, 'applicant_submit_sadr_notification', 'Submitted report :reference number', '<p>Thank you for submitting the SADR report to MCAZ. An acknowledgement email has been sent to <em>:reporter_email</em>. Your reference number is <strong>:reference_number</strong>.</p>\r\n', 'notification', 'System notification generated for the user when they submit a new SADR Report', '2017-12-05 22:15:10', '2017-12-16 19:33:50', NULL),
(3, 'applicant_delete_message', NULL, '<p>test to soft delete</p>\r\n', 'email', '', '2017-12-05 00:00:00', '2017-12-05 22:15:51', NULL),
(4, 'manager_submit_sadr_email', 'New SADR report :reference_number', '<p>Dear :name,</p>\r\n\r\n<p>A new SADR report :reference_number, has been submitted from the online portal and is ready for review.</p>\r\n\r\n<p>Regards,</p>\r\n\r\n<p>MCAZ PV</p>\r\n', 'email', 'Email sent to evaluators when a new ADR report is submitted.', '2017-12-09 14:39:08', '2017-12-16 19:37:59', NULL),
(5, 'manager_submit_sadr_notification', 'New SADR report :reference_number', '<p>New SADR report :reference_number submitted on :submitted_date is ready for review.</p>\r\n', 'notification', 'Notification sent to evaluators on new ADR report submitted.', '2017-12-09 14:41:28', '2017-12-16 19:39:27', NULL),
(6, 'manager_assign_evaluator_email', 'New report :reference_number for review', '<p>Dear Sir/Madam,</p>\r\n\r\n<p>You have been assigned a new Report :reference_number for review :assigned_by_name.</p>\r\n\r\n<p>Regards,</p>\r\n\r\n<p>MCAZ</p>\r\n', 'email', 'Email sent to evaluator when a new report has been assigned to them.', '2017-12-12 15:17:08', '2017-12-18 00:16:33', NULL),
(7, 'manager_assign_evaluator_notification', 'New report :reference_number for review', '<p>Newly assigned Report :reference_number for review. Assigned by :assigned_by_name</p>\r\n', 'notification', 'Notification sent to Evaluator when they have been assigned a new SADR report for review.', '2017-12-12 15:24:26', '2017-12-18 00:16:55', NULL),
(8, 'manager_assign_notification', 'Assigned :reference_number to :assigned_to_name', '<p>Assigned :reference_number to :assigned_to_name.</p>\r\n', 'notification', 'Notification sent to Manager when they have assigned an evaluator a new report for review.', '2017-12-12 15:30:10', '2017-12-18 00:17:49', NULL),
